parameters.jsCode:
/**
 * Split Location (robust)
 * Input:  { locations: [ { name, city, resolverUrl, listId }, ... ] }
 * Output: one item per location with name, city, resolverUrl, listId
 */

function s(v) { return (v == null ? '' : String(v)).trim(); }

// Accept array from Set Locations
const locs = Array.isArray($json.locations) ? $json.locations : [];

if (!Array.isArray(locs) || locs.length === 0) {
  throw new Error('Split Location: expected array field "locations" from Set Locations.');
}

// Map of default resolverUrls if you don’t want to type them in Set Locations
const ORIGIN_BY_CITY = {
  dallas:     'https://dallas.hibid.com',
  houston:    'https://houston.hibid.com',
  sacramento: 'https://rivercityfurnitureauction.hibid.com',
  indiana:    'https://greatlakes.hibid.com',
  indy:       'https://greatlakes.hibid.com'
};

const out = locs.map(raw => {
  const name   = s(raw.name);
  const city   = s(raw.city || raw.name);
  let resolver = s(raw.resolverUrl);
  const listId = s(raw.listId);

  if (!resolver) {
    const key = city.toLowerCase();
    resolver = ORIGIN_BY_CITY[key] || '';
  }

  return {
    json: { name, city, resolverUrl: resolver, listId }
  };
});

return out;

const N = 12; // keep top N items

// Make relative links absolute against the catalog URL
const toAbs = (u, baseUrl) => {
  if (!u) return '';
  if (/^https?:\/\//i.test(u)) return u;
  try {
    const base = new URL(baseUrl);
    return new URL(u, base.origin).href;
  } catch {
    return u;
  }
};

const links  = $json.itemLink  || [];
const imgs   = $json.imgSrc    || [];
const titles = $json.itemTitle || [];
const prices = $json.price     || [];

const items = [];
for (let i = 0; i < titles.length; i++) {
  const title = (titles[i] || '').trim();
  const href = toAbs(links[i], $json.catalogUrl);
  const imageUrl = toAbs(imgs[i], $json.catalogUrl);
  const price = (prices[i] || '').trim();
  if (title && href && imageUrl) {
    items.push({ title, href, imageUrl, price });
  }
}

return [{
  ...$json,                   // keep name, city, listId, catalogUrl
  items: items.slice(0, N)    // limit email length
}];

/**
 * Render Email HTML
 * Inputs expected on $json:
 *   name        -> city name (e.g., "Dallas")
 *   listId      -> Constant Contact list id for the city
 *   catalogUrl  -> resolved catalog URL for the city
 *   items[]     -> array of { title, href, imageUrl, price }
 *   subject?    -> optional subject from AI node
 *   intro?      -> optional intro line from AI node
 *   isMorning?  -> optional flag to tweak UTM & tone
 *
 * Outputs on $json:
 *   email_html  -> full HTML (custom code) for Constant Contact
 *   subject     -> final subject line
 *   preheader   -> short preview text
 *
 * Notes:
 * - Includes [[trackingImage]] for Constant Contact open tracking.
 * - Adds basic UTM params to item links for AM/PM and city tracking.
 * - Mobile-safe single-column layout (wide compatibility).
 */

const city       = ($json.name || 'Auction').trim();
const items      = Array.isArray($json.items) ? $json.items : [];
const isMorning  = !!$json.isMorning; // if you set this earlier
const catalogUrl = $json.catalogUrl || '';

/* ---------- helpers ---------- */
function esc(s = '') {
  return String(s)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;');
}

function ensureAbs(u) {
  if (!u) return '';
  if (/^https?:\/\//i.test(u)) return u;
  try {
    const base = new URL(catalogUrl || 'https://rivercityfurnitureauction.hibid.com/');
    return new URL(u, base).href;
  } catch {
    return u;
  }
}

function addUtm(u, citySlug, when) {
  const url = ensureAbs(u);
  const sep = url.includes('?') ? '&' : '?';
  return `${url}${sep}utm_source=cc&utm_medium=email&utm_campaign=auction_${citySlug}_${when}`;
}

function pick(arr = [], n = 12) {
  return arr.slice(0, n);
}

function words(s = '', max = 25) {
  const parts = s.split(/\s+/).filter(Boolean);
  return parts.slice(0, max).join(' ');
}

const citySlug = city.toLowerCase().replace(/\s+/g, '-');
const whenTag  = isMorning ? 'am' : 'pm';

/* ---------- subject + intro + preheader ---------- */
let subject = ($json.subject || '').trim();
if (!subject) {
  const opts = [
    `${city} Auction Highlights`,
    `${city} Fresh Finds Today`,
    `${city} Deals You’ll Love`,
    `${city} Top Picks Right Now`,
  ];
  subject = opts[Math.floor(Math.random() * opts.length)];
}
// keep subject to ~8 words, just in case
subject = words(subject, 8);

let intro = ($json.intro || '').trim();
if (!intro) {
  const top = pick(items, 3).map(i => i.title).filter(Boolean);
  const sample = top.join(', ');
  intro = isMorning
    ? `Early picks in ${city}: ${sample}.`
    : `Tonight in ${city}: ${sample}.`;
}
intro = words(intro, 25);

const preheader = words(
  items.length
    ? `New lots in ${city}. Pickup available. No reserves.`
    : `Browse ${city} auction items now.`,
  18
);

/* ---------- build item cards (single column for max compatibility) ---------- */
const cardsHTML = pick(items, 12).map((i) => {
  const title = esc(i.title || '');
  const price = (i.price || '').trim();
  const href  = addUtm(i.href || '#', citySlug, whenTag);
  const img   = ensureAbs(i.imageUrl || '');

  return `
  <tr>
    <td style="padding:12px 0;">
      <a href="${esc(href)}" target="_blank">
        <img src="${esc(img)}" alt="${title}" style="width:100%;max-width:560px;display:block;border:0;outline:0;text-decoration:none;" />
      </a>
      <div style="font:14px Arial, Helvetica, sans-serif;margin-top:8px;color:#111;line-height:1.4;">
        <strong>${title}</strong><br/>
        ${price ? `<span>${esc(price)}</span><br/>` : ``}
        <a href="${esc(href)}" target="_blank" style="text-decoration:underline;color:#1155cc;">View on HiBid →</a>
      </div>
    </td>
  </tr>`;
}).join('\n');

/* ---------- final HTML (custom code email) ---------- */
const email_html = `<!doctype html>
<html>
  <head>
    <meta http-equiv="Content-Type" content="text/html; charset=utf-8">
    <meta name="viewport" content="width=device-width,initial-scale=1">
    <title>${esc(subject)}</title>
    <!-- Basic, email-safe styles only -->
  </head>
  <body style="margin:0;padding:0;background:#ffffff;">
    [[trackingImage]]
    <center>
      <table role="presentation" cellpadding="0" cellspacing="0" width="100%" style="max-width:600px;margin:0 auto;">
        <tr>
          <td style="padding:18px 14px 8px 14px;font:22px Arial, Helvetica, sans-serif;color:#111;">
            ${esc(subject)}
          </td>
        </tr>

        <tr>
          <td style="padding:0 14px 16px 14px;font:16px Arial, Helvetica, sans-serif;color:#444;line-height:1.5;">
            ${esc(intro)}
          </td>
        </tr>

        <tr>
          <td style="padding:0 14px;">
            <table role="presentation" cellpadding="0" cellspacing="0" width="100%">
              ${cardsHTML || `
              <tr><td style="padding:12px 0;font:14px Arial, Helvetica, sans-serif;color:#555;">
                No items found. Please check back soon.
              </td></tr>`}
            </table>
          </td>
        </tr>

        <tr>
          <td style="padding:18px 14px 28px 14px;font:12px Arial, Helvetica, sans-serif;color:#777;line-height:1.4;">
            You’re receiving this because you opted in at River City Furniture Auction.
            Pickup required; no shipping unless noted. Unsubscribe via the link in the footer.
          </td>
        </tr>
      </table>
    </center>
  </body>
</html>`;

/* ---------- return ---------- */
return [{
  ...$json,
  email_html,
  subject,
  preheader
}];

