parameters.jsCode:
// n8n OpenAI node typically returns content at $json.choices[0].message.content
// but we’ll guard for other shapes too.
let raw = $json;
let content =
  raw?.choices?.[0]?.message?.content ??
  raw?.data?.choices?.[0]?.message?.content ??
  (typeof raw === 'string' ? raw : '');

let subject = '';
let intro = '';

try {
  const obj = JSON.parse(content);
  subject = (obj.subject || '').trim();
  intro   = (obj.intro || '').trim();
} catch (e) {
  // Fallback: try to extract "subject:" and "intro:" lines if JSON wasn’t clean
  const s = /"subject"\s*:\s*"([^"]+)"/i.exec(content);
  const i = /"intro"\s*:\s*"([^"]+)"/i.exec(content);
  subject = s ? s[1] : '';
  intro   = i ? i[1] : '';
}

// Enforce city at start, uniqueness, and length limits
const city = $json.name || '';
function startsWithCity(str, cityName) {
  return str.toLowerCase().startsWith(cityName.toLowerCase());
}
function trimWords(str, maxWords) {
  const parts = (str || '').split(/\s+/).filter(Boolean);
  return parts.slice(0, maxWords).join(' ');
}
if (!startsWithCity(subject, city)) {
  subject = `${city} ${subject}`;
}
subject = trimWords(subject, 8);

// Guarantee subjects differ across locations by including city code
// (since city is at the start, each location's subject is inherently different).
// But also ensure no empties:
if (!subject) subject = `${city} Auction Highlights`;
if (!intro)   intro   = `Fresh picks in ${city} today.`;

return [{
  ...$json,
  subject,
  intro
}];

/**
 * Choose Current Catalog
 * Input:  auctionLink[], auctionTitle[], plus `city` from Split Location
 * Output: catalogUrl (absolute https://... URL)
 */

const cityRaw = ($json.city || $json.name || '').trim();
const city = cityRaw.toLowerCase();
const links  = $json.auctionLink  || [];
const titles = $json.auctionTitle || [];

// Pick link whose title contains the city, fallback to first
let chosen = null;
for (let i = 0; i < links.length; i++) {
  const t = String(titles[i] || '').toLowerCase();
  if (city && t.includes(city)) { chosen = links[i]; break; }
}
if (!chosen && links.length) chosen = links[0];
if (!chosen) throw new Error('No catalog link found.');

// Normalize path
let path = String(chosen).trim();

// Map of city → base origin
const ORIGIN_BY_CITY = {
  dallas:     'https://dallas.hibid.com',
  houston:    'https://houston.hibid.com',
  sacramento: 'https://rivercityfurnitureauction.hibid.com',
  indiana:    'https://greatlakes.hibid.com',
  indy:       'https://greatlakes.hibid.com'
};

// Build absolute
let catalogUrl;
if (/^https?:\/\//i.test(path)) {
  catalogUrl = path;
} else {
  const origin = ORIGIN_BY_CITY[city];
  if (!origin) throw new Error(`No origin mapped for city "${cityRaw}"`);
  catalogUrl = origin + (path.startsWith('/') ? path : '/' + path);
}

return [{ ...$json, catalogUrl }];

parameters.url:
={{
  (function () {
    const raw = String($json.catalogUrl || '').trim();         // from the Set node above
    if (!raw) throw new Error('catalogUrl is empty');
    if (raw.startsWith('http')) return raw;                    // already absolute

    const resolver = String($json.resolverUrl || '').trim();   // still available from earlier
    const origin = resolver.split('/').slice(0, 3).join('/');  // e.g., https://dallas.hibid.com
    if (!origin) throw new Error('Missing resolverUrl origin');

    return origin + (raw.startsWith('/') ? raw : '/' + raw);
  })()
}}
